import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";
import Home from "./pages/Home";
import Start from "./pages/Start";
import Register from "./pages/Register";
import Jobs from "./pages/Jobs";
import Gigs from "./pages/Gigs";
import Upskill from "./pages/Upskill";
import NotFound from "./pages/NotFound";
import StudentDashboard from "./pages/student/StudentDashboard";
import StudentProfile from "./pages/student/StudentProfile";
import EmployerDashboard from "./pages/employer/EmployerDashboard";
import EmployerProfile from "./pages/employer/EmployerProfile";
import GetStarted from "./pages/GetStarted";

function RequireAuth({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/start" replace />;
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/start" element={<Start />} />
          <Route path="/register" element={<Register />} />
          <Route path="/get-started" element={<GetStarted />} />

          {/* Student-only */}
          <Route path="/student/dashboard" element={<RequireAuth><StudentDashboard /></RequireAuth>} />
          <Route path="/student/profile" element={<RequireAuth><StudentProfile /></RequireAuth>} />

          {/* Employer-only */}
          <Route path="/employer/dashboard" element={<RequireAuth><EmployerDashboard /></RequireAuth>} />
          <Route path="/employer/profile" element={<RequireAuth><EmployerProfile /></RequireAuth>} />

          {/* Shared (protected) */}
          <Route path="/jobs" element={<RequireAuth><Jobs /></RequireAuth>} />
          <Route path="/gigs" element={<RequireAuth><Gigs /></RequireAuth>} />
          <Route path="/upskill" element={<RequireAuth><Upskill /></RequireAuth>} />

          <Route path="*" element={<NotFound />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}
