import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-blue-900 text-white px-5 py-3 flex justify-between items-center shadow-lg sticky top-0 z-50">
      <div className="font-bold text-2xl tracking-tight flex items-center gap-2">
        <span role="img" aria-label="logo" className="text-yellow-400">ðŸ”—</span>
        Tech-Nect
      </div>
      <ul className="flex gap-4">
        <li><Link to="/" className="hover:text-yellow-400">Home</Link></li>
        <li><Link to="/jobs" className="hover:text-yellow-400">Jobs</Link></li>
        <li><Link to="/gigs" className="hover:text-yellow-400">Gigs</Link></li>
        <li><Link to="/upskill" className="hover:text-yellow-400">Upskill</Link></li>
        {user ? (
          <>
            {user.role === "student" && (
              <>
                <li><Link to="/student/dashboard" className="hover:text-yellow-400">Dashboard</Link></li>
                <li><Link to="/student/profile" className="hover:text-yellow-400">Profile</Link></li>
              </>
            )}
            {user.role === "employer" && (
              <>
                <li><Link to="/employer/dashboard" className="hover:text-yellow-400">Dashboard</Link></li>
                <li><Link to="/employer/profile" className="hover:text-yellow-400">Profile</Link></li>
              </>
            )}
            <li><Link to="/settings" className="hover:text-yellow-400">Settings</Link></li>
            <li>
              <button onClick={handleLogout} className="ml-2 px-3 py-1 rounded bg-yellow-500 text-blue-900 hover:bg-yellow-400">
                Logout
              </button>
            </li>
          </>
        ) : (
          <>
            <li><Link to="/login" className="hover:text-yellow-400">Login</Link></li>
            <li><Link to="/register" className="hover:text-yellow-400">Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}
