import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext"; // Add this import!
import { matchJobs } from "../utils/matchJobs";
import JobCard from "../components/JobCard";
import { AcademicCapIcon } from "@heroicons/react/24/outline";
import { motion } from "framer-motion";
import axios from "axios";

export default function Jobs() {
  const { user } = useAuth(); // Get the logged-in user and token
  // If you still use students[0], keep it. Otherwise, use user details
  // const student = students[0];
  // For demo, let's use user (who is logged in)
  const student = user;

  const [q, setQ] = useState("");
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    if (user?.token) {
      axios.get("http://localhost:5000/api/jobs", {
        headers: { Authorization: `Bearer ${user.token}` }
      })
      .then(res => setJobs(res.data))
      .catch(err => {
        setJobs([]);
        // Optionally, handle/notify error (token expired, etc)
      });
    }
  }, [user]);

  // Adjust for your real user object/skills as needed
  const matched = student && student.skills && jobs.length
    ? matchJobs(student.skills, jobs)
    : [];

  const filtered = jobs.filter(j =>
    j.title.toLowerCase().includes(q.toLowerCase())
  );

  return (
    <section className="max-w-3xl mx-auto py-10">
      <div>
        <h2>Available Jobs</h2>
        {jobs.map(job => <JobCard job={job} key={job._id || job.id} />)}
      </div>
      <motion.h2
        className="text-3xl font-bold mb-2 text-blue-900 flex items-center gap-2"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <AcademicCapIcon className="h-8 w-8 text-blue-600" />
        Matched Jobs
      </motion.h2>
      {student && (
        <p className="mb-4 text-blue-700">
          For: {student.name} ({student.university || student.email})
        </p>
      )}
      {matched.length === 0 ? (
        <div className="text-gray-500">No matches found. Try adding more skills!</div>
      ) : (
        matched.map(job => <JobCard key={job._id || job.id} job={job} />)
      )}
      <input
        value={q}
        onChange={e => setQ(e.target.value)}
        placeholder="Search jobs..."
        className="w-full border px-4 py-2 rounded mb-4 mt-4"
      />
      {filtered.map(job => <JobCard key={job._id || job.id} job={job} />)}
    </section>
  );
}
