import React from "react";
import gigs from "../data/gigs";
import GigCard from "../components/GigCard";
import { BoltIcon } from "@heroicons/react/24/outline";
import { motion } from "framer-motion";

export default function Gigs() {
    const [q, setQ] = useState("");
const filtered = jobs.filter(j => j.title.toLowerCase().includes(q.toLowerCase()));
  return (
    <section className="max-w-3xl mx-auto py-10">
      <motion.h2
        className="text-3xl font-bold mb-4 text-yellow-800 flex items-center gap-2"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <input value={q} onChange={e=>setQ(e.target.value)} placeholder="Search jobs..." />
{filtered.map(job=> <JobCard job={job} />)}
        <BoltIcon className="h-8 w-8 text-yellow-500" />
        Available Gigs
      </motion.h2>
      {gigs.map(gig => <GigCard key={gig.id} gig={gig} />)}
    </section>
  );
}
