import React, { useState } from "react";
import { useAuth } from "../../context/AuthContext";
import { AcademicCapIcon, PencilAltIcon, CheckIcon } from "@heroicons/react/outline";
import toast from "react-hot-toast";

export default function StudentProfile() {
  const { user, updateUser } = useAuth(); // You'll need an updateUser method in your context for real backend
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState(user?.name || "");
  const [skills, setSkills] = useState(user?.skills || []);
  const [newSkill, setNewSkill] = useState("");

  // Save profile handler (fake/demo logic for now)
  const handleSave = () => {
    setEditing(false);
    toast.success("Profile updated!");
    if (updateUser) updateUser({ ...user, name, skills });
  };

  // Add skill to list
  const addSkill = () => {
    if (newSkill && !skills.includes(newSkill)) {
      setSkills([...skills, newSkill]);
      setNewSkill("");
    }
  };

  // Remove skill
  const removeSkill = (skill) => setSkills(skills.filter(s => s !== skill));

  return (
    <div className="max-w-lg mx-auto my-12 p-8 bg-white rounded-xl shadow flex flex-col items-center">
      <div className="flex flex-col items-center">
        <div className="h-24 w-24 rounded-full bg-blue-200 flex items-center justify-center mb-4">
          <AcademicCapIcon className="h-12 w-12 text-blue-700" />
        </div>
        {!editing ? (
          <>
            <h2 className="text-2xl font-bold mb-2">{name}</h2>
            <div className="mb-1 text-blue-800 font-semibold">{user?.email}</div>
            <div className="mb-3 text-gray-500">Role: {user?.role}</div>
            <button
              className="flex items-center gap-1 text-blue-700 hover:underline"
              onClick={() => setEditing(true)}
            >
              <PencilAltIcon className="h-4 w-4" />
              Edit Profile
            </button>
          </>
        ) : (
          <>
            <input
              className="text-xl font-bold mb-2 border-b border-blue-300 focus:outline-none"
              value={name}
              onChange={e => setName(e.target.value)}
            />
            <div className="mb-1 text-blue-800 font-semibold">{user?.email}</div>
            <div className="mb-3 text-gray-500">Role: {user?.role}</div>
            <button
              className="flex items-center gap-1 text-green-700 hover:underline"
              onClick={handleSave}
            >
              <CheckIcon className="h-4 w-4" />
              Save Profile
            </button>
          </>
        )}
      </div>
      <div className="w-full mt-6">
        <h4 className="font-semibold text-blue-900 mb-2">Your Skills</h4>
        {!editing ? (
          <div className="flex flex-wrap gap-2">
            {skills?.length ? (
              skills.map((skill, i) => (
                <span key={i} className="bg-blue-100 px-3 py-1 rounded-full text-blue-700 text-sm">{skill}</span>
              ))
            ) : (
              <span className="text-gray-400">No skills listed yet.</span>
            )}
          </div>
        ) : (
          <div>
            <div className="flex flex-wrap gap-2 mb-2">
              {skills.map((skill, i) => (
                <span
                  key={i}
                  className="bg-blue-100 px-3 py-1 rounded-full text-blue-700 text-sm flex items-center gap-1"
                >
                  {skill}
                  <button
                    className="ml-1 text-red-400"
                    onClick={() => removeSkill(skill)}
                    type="button"
                  >âœ•</button>
                </span>
              ))}
            </div>
            <div className="flex gap-2">
              <input
                type="text"
                className="border px-2 py-1 rounded"
                placeholder="Add skill"
                value={newSkill}
                onChange={e => setNewSkill(e.target.value)}
                onKeyDown={e => e.key === "Enter" && addSkill()}
              />
              <button
                className="bg-blue-700 text-white px-3 py-1 rounded hover:bg-blue-500"
                onClick={addSkill}
                type="button"
              >
                Add
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
