import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import logo from "../assets/logo.svg"; // Adjust path if needed

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  // For NavLink active styling
  const linkClass = ({ isActive }) =>
    isActive
      ? "font-bold text-yellow-400 underline underline-offset-4"
      : "hover:text-yellow-400 transition";

  return (
    <nav className="bg-blue-900 text-white px-4 py-3 flex justify-between items-center shadow-lg sticky top-0 z-50">
      <NavLink to="/" className="flex items-center gap-2">
        <img src={logo} alt="Tech-Nect Logo" className="h-10 w-10" />
        <span className="font-black text-2xl tracking-tight">Tech-Nect</span>
      </NavLink>
      <div className="flex gap-4 items-center">
        <NavLink to="/jobs" className={linkClass}>Jobs</NavLink>
        <NavLink to="/gigs" className={linkClass}>Gigs</NavLink>
        <NavLink to="/upskill" className={linkClass}>Upskill</NavLink>
        {!user && (
          <>
            <NavLink to="/login" className={linkClass}>Login</NavLink>
            <NavLink to="/register" className={linkClass}>Register</NavLink>
          </>
        )}
        {user && (
          <>
            {user.role === "student" && (
              <>
                <NavLink to="/student/dashboard" className={linkClass}>Dashboard</NavLink>
                <NavLink to="/student/profile" className={linkClass}>Profile</NavLink>
              </>
            )}
            {user.role === "employer" && (
              <>
                <NavLink to="/employer/dashboard" className={linkClass}>Dashboard</NavLink>
                <NavLink to="/employer/profile" className={linkClass}>Profile</NavLink>
              </>
            )}
            <NavLink to="/settings" className={linkClass}>Settings</NavLink>
            <button
              onClick={() => { logout(); navigate("/login"); }}
              className="ml-2 px-3 py-1 rounded bg-yellow-500 text-blue-900 hover:bg-yellow-400 font-semibold"
            >
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
