import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";

export default function Upskill() {
  const { user } = useAuth();
  const [resources, setResources] = useState([]);

  useEffect(() => {
    if (user?.token) {
      axios
        .get("http://localhost:5000/api/upskill", {
          headers: { Authorization: `Bearer ${user.token}` },
        })
        .then((res) => setResources(res.data))
        .catch(() => setResources([]));
    }
  }, [user]);

  return (
    <section className="max-w-3xl mx-auto py-10">
      <h2 className="text-3xl font-bold mb-4 text-blue-900">Upskill Recommendations</h2>
      <p className="mb-4 text-blue-700">
        Hi {user?.name || "there"}, here are suggested resources for you:
      </p>
      <ul className="list-disc list-inside text-lg">
        {resources.length === 0 ? (
          <li>No upskill recommendations found.</li>
        ) : (
          resources.map((r, idx) => (
            <li key={r._id || idx} className="mb-2">
              <span className="font-semibold">{r.title}</span>
              {r.resourceUrl && (
                <>
                  {" – "}
                  <a
                    href={r.resourceUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                  >
                    Resource Link
                  </a>
                </>
              )}
              <div className="text-sm text-gray-600">{r.description}</div>
            </li>
          ))
        )}
      </ul>
    </section>
  );
}
