import React, { useState } from "react";
import { useAuth } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import { PlusCircleIcon } from "@heroicons/react/outline";
import axios from "axios";

export default function PostJob() {
  const { user } = useAuth();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post(
        "http://localhost:5000/api/jobs",
        { title, description, location },
        {
          headers: { Authorization: `Bearer ${user.token}` }
        }
      );
      toast.success("Job posted!");
      navigate("/employer/jobs");
    } catch {
      toast.error("Failed to post job.");
    }
    setLoading(false);
  };

  return (
    <div className="max-w-xl mx-auto py-10">
      <div className="flex items-center gap-2 mb-5">
        <PlusCircleIcon className="w-7 h-7 text-yellow-700" />
        <h2 className="text-2xl font-bold text-yellow-800">Post a New Job</h2>
      </div>
      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-xl rounded-xl p-8 space-y-5"
      >
        <div>
          <label className="block mb-1 text-yellow-700">Job Title</label>
          <input
            className="w-full border px-4 py-2 rounded"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block mb-1 text-yellow-700">Description</label>
          <textarea
            className="w-full border px-4 py-2 rounded"
            value={description}
            onChange={e => setDescription(e.target.value)}
            rows={5}
            required
          />
        </div>
        <div>
          <label className="block mb-1 text-yellow-700">Location</label>
          <input
            className="w-full border px-4 py-2 rounded"
            value={location}
            onChange={e => setLocation(e.target.value)}
            required
          />
        </div>
        <button
          type="submit"
          className={`w-full py-2 px-4 rounded-2xl font-bold text-white transition ${
            loading ? "bg-yellow-300" : "bg-yellow-700 hover:bg-yellow-600"
          }`}
          disabled={loading}
        >
          {loading ? "Posting..." : "Post Job"}
        </button>
      </form>
    </div>
  );
}
