import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import toast from "react-hot-toast";

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("student");
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    // Simulate API call for demo. Replace with actual API!
    setTimeout(() => {
      setLoading(false);
      if (!email) {
        toast.error("Please enter your email.");
        return;
      }
      login({ name: email.split("@")[0], email, role });
      toast.success("Login successful!");
      navigate(role === "student" ? "/student/dashboard" : "/employer/dashboard");
    }, 1000);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-[70vh] px-4">
      <form
        onSubmit={handleLogin}
        className="bg-white shadow-xl rounded-xl p-8 max-w-sm w-full"
      >
        <h2 className="text-2xl font-bold mb-6 text-blue-900">Login</h2>
        <div className="mb-4">
          <label className="block mb-1 text-blue-700">Email</label>
          <input
            className="w-full border px-4 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-300"
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            autoComplete="username"
          />
        </div>
        <div className="mb-6">
          <label className="block mb-1 text-blue-700">Role</label>
          <select
            className="w-full border px-4 py-2 rounded"
            value={role}
            onChange={e => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="employer">Employer</option>
          </select>
        </div>
        <button
          type="submit"
          className={`w-full py-2 px-4 rounded font-bold text-white ${loading ? 'bg-blue-300' : 'bg-blue-700 hover:bg-blue-500'}`}
          disabled={loading}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
}
