import React from "react";
import { useAuth } from "../../context/AuthContext";
import ChatWindow from "../../components/ChatWindow";

export default function EmployerProfile() {
  const { user } = useAuth();
  return (
    <div className="max-w-lg mx-auto my-12 p-8 bg-white rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-2">Your Profile</h2>
      <div className="mb-3"><b>Name:</b> {user?.name}</div>
      <div className="mb-3"><b>Email:</b> {user?.email}</div>
      <div className="mb-3"><b>Role:</b> {user?.role}</div>
      <ChatWindow user2={user} />
      {/* Company info/edit form could go here */}
    </div>
  );
}
import { useState } from "react";
const [jobs, setJobs] = useState([]);
const [newJob, setNewJob] = useState({title:"", desc:""});
// Render a form:
<div>
  <form onSubmit={e => {e.preventDefault(); setJobs([...jobs, newJob]); setNewJob({title:"",desc:""});}}>
    <input value={newJob.title} onChange={e=>setNewJob({...newJob,title:e.target.value})} placeholder="Job Title"/>
    <input value={newJob.desc} onChange={e=>setNewJob({...newJob,desc:e.target.value})} placeholder="Description"/>
    <button>Add Job</button>
  </form>
  {/* Render jobs */}
  <ul>{jobs.map((j,i)=><li key={i}>{j.title}: {j.desc}</li>)}</ul>
</div>
