import React from "react";
import { useAuth } from "../../context/AuthContext";

export default function StudentProfile() {
  const { user } = useAuth();

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];
    // You can add logic here to upload the file or update the user photo
    // For now, just log the file to avoid errors
    console.log("Selected file:", file);
  };

  return (
    <div className="max-w-lg mx-auto my-12 p-8 bg-white rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-2">Your Profile</h2>
      <input type="file" accept="image/*" onChange={handlePhotoUpload} />
      {user.photo && <img src={user.photo} alt="Profile" className="h-24 w-24 rounded-full" />}
      <div className="mb-3"><b>Name:</b> {user?.name}</div>
      <div className="mb-3"><b>Email:</b> {user?.email}</div>
      <div className="mb-3"><b>Role:</b> {user?.role}</div>
      <input placeholder="Skill" className="border p-2 rounded mb-3" />
      <input placeholder="Project Title" className="border p-2 rounded mb-3" />
      <input placeholder="GitHub URL" className="border p-2 rounded mb-3" />
      {/* CV upload/edit form could go here */}
    </div>
    
  );
}
