import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <nav className="bg-blue-900 text-white px-4 py-3 flex justify-between items-center shadow-lg sticky top-0 z-50">
      <NavLink to="/" className="font-black text-2xl tracking-tight flex items-center gap-2">
        <span role="img" aria-label="logo" className="text-yellow-400">ðŸ”—</span>
        Tech-Nect
      </NavLink>
      <div className="flex gap-4 items-center">
        <NavLink to="/jobs" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Jobs</NavLink>
        <NavLink to="/gigs" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Gigs</NavLink>
        <NavLink to="/upskill" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Upskill</NavLink>
        {!user && (
          <>
            <NavLink to="/login" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Login</NavLink>
            <NavLink to="/register" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Register</NavLink>
          </>
        )}
        {user && (
          <>
            {user.role === "student" && (
              <>
                <NavLink to="/student/dashboard" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Dashboard</NavLink>
                <NavLink to="/student/profile" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Profile</NavLink>
              </>
            )}
            {user.role === "employer" && (
              <>
                <NavLink to="/employer/dashboard" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Dashboard</NavLink>
                <NavLink to="/employer/profile" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Profile</NavLink>
              </>
            )}
            <NavLink to="/settings" className={({ isActive }) => isActive ? "font-bold text-yellow-400" : "hover:text-yellow-400"}>Settings</NavLink>
            <button
              onClick={() => { logout(); navigate("/login"); }}
              className="ml-2 px-3 py-1 rounded bg-yellow-500 text-blue-900 hover:bg-yellow-400 font-semibold"
            >
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
