import React, { useState, useEffect } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { BriefcaseIcon } from "@heroicons/react/24/outline";

export default function JobCard({ job }) {
  const [jobs, setJobs] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:4000/api/jobs").then(res => setJobs(res.data));
  }, []);
return (
  <>
  ,
    <h2>Available Jobs</h2>
    {jobs.map(job => <JobCard job={job} key={job.id} />)}
    <motion.div
      className="bg-white rounded-2xl shadow-xl p-6 mb-5 border-l-4 border-blue-400 hover:scale-105 transition-all"
      whileHover={{ y: -8, boxShadow: "0 12px 32px rgba(0, 0, 128, 0.1)" }}
    >
      <div className="flex items-center gap-3 mb-2">
        <BriefcaseIcon className="h-7 w-7 text-blue-600" />
        <span className="font-bold text-xl">{job.title}</span>
      </div>
      <div className="text-blue-700">{job.company} <span className="text-sm text-gray-600">| {job.location}</span></div>
      <div className="my-2 text-slate-700">{job.description}</div>
      <div className="mb-1">
        <strong>Skills:</strong> {job.required_skills.join(", ")}
      </div>
      {job.matchScore !== undefined && (
        <>
          <div className="mt-2">
            <span className="px-3 py-1 bg-blue-100 rounded-full text-blue-800 font-semibold">
              Match: {(job.matchScore * 100).toFixed(0)}%
            </span>
          </div>
          <button
            className="bg-green-600 text-white px-4 py-2 rounded mt-3 hover:bg-green-500"
            onClick={() => alert('Application submitted!')}
          >
            Apply
          </button>
        </>
      )}
    </motion.div>
  </>
);
}
