import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

export default function Home() {
  return (
    <motion.section
      className="relative py-16 px-4 min-h-[70vh] flex flex-col items-center justify-center bg-gradient-to-br from-blue-100 via-blue-200 to-white"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <motion.h1
        className="text-4xl md:text-6xl font-black text-blue-900 mb-5 drop-shadow-xl text-center"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 0.7 }}
      >
        ðŸš€ Tech-Nect Uganda
      </motion.h1>
      <motion.p
        className="text-xl md:text-2xl text-blue-700 mb-10 max-w-xl text-center"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4, duration: 0.7 }}
      >
        Connecting Ugandan tech talent to real jobs, gigs, and growth opportunities.<br />
        Build your futureâ€”start here.
      </motion.p>
      <motion.div
        className="flex flex-col md:flex-row gap-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8, duration: 0.7 }}
      >
        <Link to="/jobs" className="bg-blue-800 text-white px-8 py-3 rounded-xl shadow-lg hover:bg-blue-600 hover:scale-105 transition font-semibold">See Jobs</Link>
        <Link to="/gigs" className="bg-yellow-400 text-blue-900 px-8 py-3 rounded-xl shadow-lg hover:bg-yellow-300 hover:scale-105 transition font-semibold">See Gigs</Link>
        <Link to="/upskill" className="bg-green-400 text-white px-8 py-3 rounded-xl shadow-lg hover:bg-green-300 hover:scale-105 transition font-semibold">Upskill</Link>
      </motion.div>
      <img
        src="https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=900&q=80"
        alt="Ugandan Tech"
        className="rounded-3xl shadow-2xl w-full max-w-lg mt-14"
        style={{ border: "6px solid #e0e7ff" }}
      />
    </motion.section>
  );
}
