import React from "react";
import { useAuth } from "../../context/AuthContext";
import { Link } from "react-router-dom";
import { AcademicCapIcon, BriefcaseIcon, LightningBoltIcon } from "@heroicons/react/outline";
import { motion } from "framer-motion";

export default function StudentDashboard() {
  const { user } = useAuth();
  return (
    <motion.div
      className="max-w-3xl mx-auto p-8"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <h2 className="text-3xl font-bold mb-3">Welcome, {user?.name || "Student"}!</h2>
      <div className="mb-3">You are logged in as a <b>Student</b>.</div>
      <div className="flex flex-wrap gap-4 mt-8 mb-8">
        <Link to="/student/profile" className="bg-blue-700 text-white px-6 py-4 rounded-2xl shadow hover:bg-blue-500 flex items-center gap-2">
          <AcademicCapIcon className="h-6 w-6" />
          Profile
        </Link>
        <Link to="/jobs" className="bg-yellow-400 text-blue-900 px-6 py-4 rounded-2xl shadow hover:bg-yellow-300 flex items-center gap-2">
          <BriefcaseIcon className="h-6 w-6" />
          See Jobs
        </Link>
        <Link to="/gigs" className="bg-green-400 text-white px-6 py-4 rounded-2xl shadow hover:bg-green-300 flex items-center gap-2">
          <LightningBoltIcon className="h-6 w-6" />
          Explore Gigs
        </Link>
      </div>
      <div className="mt-10">
        <div className="text-lg text-blue-900 font-semibold mb-4">
          Start applying for jobs or gigs to build your career!
        </div>
        {/* You could add recent applications, suggestions, or activity feed here */}
      </div>
    </motion.div>
  );
}
