import React, { useEffect, useState } from "react";
import students from "../data/students";
import { matchJobs } from "../utils/matchJobs";
import JobCard from "../components/JobCard";
import { AcademicCapIcon } from "@heroicons/react/24/outline";
import { motion } from "framer-motion";
import axios from "axios";

export default function Jobs() {
  const student = students[0];
  const [q, setQ] = useState("");
  const [jobs, setJobs] = useState([]);
   useEffect(() => {
    axios.get("http://localhost:4000/api/jobs").then(res => setJobs(res.data));
  }, []);
  const matched = matchJobs(student.skills, jobs);

const filtered = jobs.filter(j => j.title.toLowerCase().includes(q.toLowerCase()));

  return (
    <section className="max-w-3xl mx-auto py-10">
      <div>
      <h2>Available Jobs</h2>
      {jobs.map(job => <JobCard job={job} key={job.id} />)}
      <div>{job.title}</div>
      <button onClick={handleApply}>Apply</button>
    </div>
      <motion.h2
        className="text-3xl font-bold mb-2 text-blue-900 flex items-center gap-2"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <AcademicCapIcon className="h-8 w-8 text-blue-600" />
        Matched Jobs
      </motion.h2>
      <p className="mb-4 text-blue-700">For: {student.name} ({student.university})</p>
      {matched.length === 0 ? (
        <div className="text-gray-500">No matches found. Try adding more skills!</div>
      ) : (
        matched.map(job => <JobCard key={job.id} job={job} />)
      )}
      <input value={q} onChange={e=>setQ(e.target.value)} placeholder="Search jobs..." />
{filtered.map(job=> <JobCard job={job} />)}
    </section>
  );
}
