import React from "react";
import { useAuth } from "../../context/AuthContext";
import { Link } from "react-router-dom";
import { UserGroupIcon, PlusCircleIcon } from "@heroicons/react/24/outline";
import { motion } from "framer-motion";

export default function EmployerDashboard() {
  const { user } = useAuth();
  return (
    <motion.div
      className="max-w-3xl mx-auto p-8"
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <h2 className="text-3xl font-bold mb-3">Hello, {user?.name || "Employer"}!</h2>
      <div className="mb-3">You are logged in as an <b>Employer</b>.</div>
      <div className="flex flex-wrap gap-4 mt-8 mb-8">
        <Link to="/employer/profile" className="bg-blue-700 text-white px-6 py-4 rounded-2xl shadow hover:bg-blue-500 flex items-center gap-2">
          <UserGroupIcon className="h-6 w-6" />
          Profile
        </Link>
        <Link to="/jobs" className="bg-yellow-400 text-blue-900 px-6 py-4 rounded-2xl shadow hover:bg-yellow-300 flex items-center gap-2">
          <PlusCircleIcon className="h-6 w-6" />
          Post Job
        </Link>
        <Link to="/gigs" className="bg-green-400 text-white px-6 py-4 rounded-2xl shadow hover:bg-green-300 flex items-center gap-2">
          <PlusCircleIcon className="h-6 w-6" />
          Post Gig
        </Link>
      </div>
      <div className="mt-10">
        <div className="text-lg text-blue-900 font-semibold mb-4">
          Post new jobs or gigs and manage your applicants here!
        </div>
        {/* Add activity feed, applicant stats, or quick tips here */}
      </div>
    </motion.div>
  );
}
