import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import GigCard from "../components/GigCard";
import { BoltIcon } from "@heroicons/react/24/outline";
import { motion } from "framer-motion";
import axios from "axios";

export default function Gigs() {
  const { user } = useAuth();
  const [gigs, setGigs] = useState([]);

  useEffect(() => {
    if (user?.token) {
      axios
        .get("http://localhost:5000/api/gigs", {
          headers: { Authorization: `Bearer ${user.token}` },
        })
        .then((res) => setGigs(res.data))
        .catch((err) => {
          setGigs([]);
          // Optional: handle error, e.g. show a toast or message
        });
    }
  }, [user]);

  return (
    <section className="max-w-3xl mx-auto py-10">
      <motion.h2
        className="text-3xl font-bold mb-4 text-yellow-800 flex items-center gap-2"
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <BoltIcon className="h-8 w-8 text-yellow-500" />
        Available Gigs
      </motion.h2>
      {gigs.length === 0 ? (
        <div className="text-gray-500">No gigs found.</div>
      ) : (
        gigs.map((gig) => <GigCard key={gig._id || gig.id} gig={gig} />)
      )}
    </section>
  );
}
