import React, { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
// import logo from "../assets/logo.svg"; // Use only if you want image logo fallback

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);

  const linkClass = ({ isActive }) =>
    isActive
      ? "font-bold text-yellow-400 underline underline-offset-4"
      : "hover:text-yellow-400 transition";

  // Get user initials (up to 2 letters)
  const getInitials = (name = "") =>
    name
      ? name.split(" ").map(n => n[0]).join("").substring(0,2).toUpperCase()
      : "U";

  return (
    <nav className="bg-blue-900 text-white px-4 py-3 flex justify-between items-center shadow-lg sticky top-0 z-50">
      <NavLink to="/" className="flex items-center gap-2">
        {/* Logo with initials */}
        <div className="bg-yellow-400 text-blue-900 rounded-full w-10 h-10 flex items-center justify-center font-black text-xl shadow-md">
          TN
        </div>
        {/* If you want to use your image logo instead, swap out the above div for: */}
        {/* <img src={logo} alt="Tech-Nect Logo" className="h-10 w-10" /> */}
        <span className="font-black text-2xl tracking-tight">Tech-Nect</span>
      </NavLink>

      {/* Hamburger for mobile */}
      <button
        onClick={() => setOpen(!open)}
        className="md:hidden block text-yellow-400 focus:outline-none"
      >
        <svg width="28" height="28" fill="none" viewBox="0 0 24 24">
          <path d="M4 6h16M4 12h16M4 18h16" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
        </svg>
      </button>

      {/* Links for desktop */}
      <div className="md:flex gap-4 items-center hidden">
        {/* User greeting and avatar */}
        {user && (
          <>
            <span className="flex items-center gap-2 text-yellow-300 font-semibold mr-2">
              <div className="bg-yellow-400 text-blue-900 rounded-full w-8 h-8 flex items-center justify-center font-black text-lg">
                {getInitials(user.name)}
              </div>
              Hi, {user.name.split(" ")[0]}!
            </span>
            <NavLink to="/jobs" className={linkClass}>Jobs</NavLink>
            <NavLink to="/gigs" className={linkClass}>Gigs</NavLink>
            <NavLink to="/upskill" className={linkClass}>Upskill</NavLink>
            {user.role === "student" && (
              <>
                <NavLink to="/student/dashboard" className={linkClass}>Dashboard</NavLink>
                <NavLink to="/student/profile" className={linkClass}>Profile</NavLink>
              </>
            )}
            {user.role === "employer" && (
              <>
                <NavLink to="/employer/dashboard" className={linkClass}>Dashboard</NavLink>
                <NavLink to="/employer/profile" className={linkClass}>Profile</NavLink>
              </>
            )}
            <NavLink to="/settings" className={linkClass}>Settings</NavLink>
            <button
              onClick={() => { logout(); navigate("/login"); }}
              className="ml-2 px-3 py-1 rounded bg-yellow-500 text-blue-900 hover:bg-yellow-400 font-semibold"
            >
              Logout
            </button>
          </>
        )}
        {!user && (
          <>
            <NavLink to="/login" className={linkClass}>Login</NavLink>
            <NavLink to="/register" className={linkClass}>Register</NavLink>
          </>
        )}
      </div>

      {/* Links for mobile (toggle) */}
      {open && (
        <div className="absolute top-16 right-4 bg-blue-800 text-white rounded-lg p-4 flex flex-col gap-3 z-50 md:hidden">
          {user && (
            <>
              <span className="flex items-center gap-2 text-yellow-300 font-semibold mb-2">
                <div className="bg-yellow-400 text-blue-900 rounded-full w-8 h-8 flex items-center justify-center font-black text-lg">
                  {getInitials(user.name)}
                </div>
                Hi, {user.name.split(" ")[0]}!
              </span>
              <NavLink to="/jobs" className={linkClass} onClick={() => setOpen(false)}>Jobs</NavLink>
              <NavLink to="/gigs" className={linkClass} onClick={() => setOpen(false)}>Gigs</NavLink>
              <NavLink to="/upskill" className={linkClass} onClick={() => setOpen(false)}>Upskill</NavLink>
              {user.role === "student" && (
                <>
                  <NavLink to="/student/dashboard" className={linkClass} onClick={() => setOpen(false)}>Dashboard</NavLink>
                  <NavLink to="/student/profile" className={linkClass} onClick={() => setOpen(false)}>Profile</NavLink>
                </>
              )}
              {user.role === "employer" && (
                <>
                  <NavLink to="/employer/dashboard" className={linkClass} onClick={() => setOpen(false)}>Dashboard</NavLink>
                  <NavLink to="/employer/profile" className={linkClass} onClick={() => setOpen(false)}>Profile</NavLink>
                </>
              )}
              <NavLink to="/settings" className={linkClass} onClick={() => setOpen(false)}>Settings</NavLink>
              <button
                onClick={() => { logout(); navigate("/login"); setOpen(false); }}
                className="mt-2 px-3 py-1 rounded bg-yellow-500 text-blue-900 hover:bg-yellow-400 font-semibold"
              >
                Logout
              </button>
            </>
          )}
          {!user && (
            <>
              <NavLink to="/login" className={linkClass} onClick={() => setOpen(false)}>Login</NavLink>
              <NavLink to="/register" className={linkClass} onClick={() => setOpen(false)}>Register</NavLink>
            </>
          )}
        </div>
      )}
    </nav>
  );
}
