import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("student");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  function handleSubmit(e) {
    e.preventDefault();
    login({ email, role, name });
    if (role === "student") {
      navigate("/student/dashboard");
    } else {
      navigate("/employer/dashboard");
    }
  }

  return (
    <div className="max-w-sm mx-auto my-16 p-8 bg-white rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-6">Register</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <select value={role} onChange={e => setRole(e.target.value)} className="p-2 rounded border">
          <option value="student">Student</option>
          <option value="employer">Employer</option>
        </select>
        <input
          required
          type="text"
          placeholder="Full Name"
          className="p-2 rounded border"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <input
          required
          type="email"
          placeholder="Email"
          className="p-2 rounded border"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <input
          required
          type="password"
          placeholder="Password"
          className="p-2 rounded border"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button className="bg-green-600 text-white py-2 rounded font-bold hover:bg-green-400">Register</button>
      </form>
    </div>
  );
}
