import { motion } from "framer-motion";
import logo from "../assets/logo.png"; // place your logo here or use public/logo.png

export default function HeroSection({ onGetStarted }) {
  return (
    <section className="relative flex flex-col items-center justify-center min-h-[70vh] w-full bg-gradient-to-br from-blue-100 via-sky-200 to-indigo-100 text-slate-900 overflow-hidden">
      {/* Decorative Bubbles */}
      <div className="absolute top-0 left-0 w-32 h-32 bg-blue-300 opacity-20 rounded-full blur-2xl animate-pulse"></div>
      <div className="absolute bottom-0 right-0 w-44 h-44 bg-indigo-400 opacity-20 rounded-full blur-2xl animate-ping"></div>

      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="flex flex-col items-center z-10"
      >
        <img
          src={logo}
          alt="App Logo"
          className="w-20 h-20 mb-4 rounded-full shadow-lg animate-bounce bg-white p-2"
          draggable={false}
        />
        <h1 className="text-4xl font-extrabold mb-2">Welcome to Tech-Nect!</h1>
        <p className="text-xl text-slate-700 max-w-xl mb-6 text-center">
          Connect. Create. Collaborate.<br />
          Elevate your workflow with our all-in-one platform.
        </p>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.97 }}
          className="px-8 py-3 bg-blue-600 text-white rounded-2xl shadow-lg font-semibold"
          onClick={onGetStarted}
        >
          Get Started
        </motion.button>
      </motion.div>
    </section>
  );
}
