import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    role: "student"
  });
  const [loading, setLoading] = useState(false);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    setTimeout(() => {
      setLoading(false);
      if (!form.name || !form.email || !form.password) {
        alert("Please fill in all fields.");
        return;
      }
      // Replace with API register
      alert("Registration successful! Please log in.");
      navigate("/start");
    }, 1000);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white rounded-xl shadow-xl p-8 space-y-4 max-w-md w-full mx-auto"
    >
      <h2 className="text-2xl font-bold mb-2 text-blue-900">Register</h2>
      <input
        name="name"
        value={form.name}
        onChange={handleChange}
        className="w-full border px-4 py-2 rounded"
        placeholder="Name"
      />
      <input
        name="email"
        type="email"
        value={form.email}
        onChange={handleChange}
        className="w-full border px-4 py-2 rounded"
        placeholder="Email"
      />
      <input
        name="password"
        type="password"
        value={form.password}
        onChange={handleChange}
        className="w-full border px-4 py-2 rounded"
        placeholder="Password"
      />
      <select
        name="role"
        value={form.role}
        onChange={handleChange}
        className="w-full border px-4 py-2 rounded"
      >
        <option value="student">Student</option>
        <option value="employer">Employer</option>
      </select>
      <button
        type="submit"
        className={`w-full py-2 px-4 rounded font-bold text-white ${loading ? 'bg-blue-300' : 'bg-blue-700 hover:bg-blue-500'}`}
        disabled={loading}
      >
        {loading ? "Registering..." : "Register"}
      </button>
      <div className="text-xs text-center mt-2">
        Already have an account?{" "}
        <button
          className="text-blue-600 underline"
          type="button"
          onClick={() => navigate("/start")}
        >
          Login
        </button>
      </div>
    </form>
  );
}
