import React from "react";
import { useAuth } from "../../context/AuthContext";
import { Link } from "react-router-dom";

export default function EmployerDashboard() {
  const { user } = useAuth();
  return (
    <div className="max-w-3xl mx-auto p-8">
      <h2 className="text-3xl font-bold mb-3">Hello, {user?.name}!</h2>
      <div className="mb-3">You are logged in as an <b>Employer</b>.</div>
      <div className="flex gap-3 mt-6">
        <Link to="/employer/profile" className="bg-blue-700 text-white px-5 py-2 rounded shadow hover:bg-blue-500">Profile</Link>
        <Link to="/settings" className="bg-gray-200 text-gray-700 px-5 py-2 rounded shadow hover:bg-gray-300">Settings</Link>
      </div>
      <div className="mt-10">
        {/* Place for posting jobs or viewing applicants */}
        <p className="text-lg text-blue-900 font-semibold">Post a job or browse tech talent!</p>
      </div>
    </div>
  );
}
