import React from "react";
import { useAuth } from "../../context/AuthContext";
import { Link } from "react-router-dom";
import { useState } from "react";


export default function StudentDashboard() {
  const { user } = useAuth();
  const [appliedJobs] = useState([]);
  return (
    <div>
      <h2 className="text-3xl font-bold mb-3">Welcome, {user?.name}!</h2>
      <div className="mb-3">You are logged in as a <b>Student</b>.</div>
      <div className="flex gap-3 mt-6">
        <Link to="/student/profile" className="bg-blue-700 text-white px-5 py-2 rounded shadow hover:bg-blue-500">Profile</Link>
        <Link to="/settings" className="bg-gray-200 text-gray-700 px-5 py-2 rounded shadow hover:bg-gray-300">Settings</Link>
      </div>
      {appliedJobs.length > 0 && (
        <div className="my-4">
          <h4 className="font-semibold">Your Applications:</h4>
          <ul>{appliedJobs.map(j => <li key={j.id}>{j.title}</li>)}</ul>
        </div>
      )}
      <div className="mt-10">
        {/* Place for job recommendations or activity feed */}
        <p className="text-lg text-blue-900 font-semibold">Start applying for jobs or gigs!</p>
      </div>
    </div>
  );
}
